  function ShowNotesFetch() {
    fetch("http://localhost:3100/notes").then((res) => {
      if (res.ok) return res.json();
    })
    .then((data)=>{

      setNotes(data);

    })
  }
  useEffect(()=>{
    ShowNotesFetch();
  },[]);


  return (
    <div className="container">
      <div className="row">
