/* eslint-disable react-hooks/exhaustive-deps */
/* eslint-disable no-undef */
/* eslint-disable no-unused-vars */
import React,{useState,useEffect} from "react";

function AddNotes() {
  const [note,setNotes]=useState('');
  const notes={
    note,
  }
  const submitHandler=(e)=>{
    e.preventDefault();
    fetch("http://localhost:3100/notes",{
      method:'POST',
      headers :{
        'Content-Type':'application/json',
      },
      body:Json.stringify(notes),
    })
    .then((res)=>{
      if(res.ok)
        return res.json();
    })
    .then((data)=>{
      setNotes(data);
    })

  }
  return (
    <div className="form-group">
      <p className="text-justify m-3">Enter the Notes</p>
    <textarea className="form-control w-50 m-3" id="exampleFormControlTextarea1" rows="3" onChange={e=>setNotes(e.target.value)}></textarea>
    <button className="btn btn-success m-3" onSubmit={submitHandler(e)}>Add Notes</button>
  </div>
  );
}

export default AddNotes;
