import React,{useState,useEffect} from "react";
import "../App.css";

function ShowNotes() {
  const [text, setText] = useState([])
  function ShowNotesFetch() {
    fetch("http://localhost:3100/notes").then((res) => {
      if (res.ok) return res.json();
    })
    .then((data)=>{

      setText(data);

    })
  }
  useEffect(()=>{
    ShowNotesFetch();
  },[]);


  return (
    <div className="card m-4 w-50 ">
      <div className="card-body">
        {text.map((note, index) => {
          return <p key={index} className="card-text">{note}</p>;
        })}

        <button className="editbtn">Edit Note</button>
      </div>
    </div>
  );
}

export default ShowNotes;
