/* eslint-disable no-const-assign */
/* eslint-disable react/style-prop-object */
import React, { useState, useEffect } from "react";
import "../App.css";

function ShowNotes() {
  let [notes, setNotes] = useState([]);
  const[readMore,setReadMore]=useState(false);
const deleteNoteHandler=(id)=>{
    const newNotes=notes.filter((note)=>note.id!==id)
    notes=[...newNotes];

  }  

  function ShowNotesFetch() {
    fetch("http://localhost:3100/notes")
      .then((res) => {
        if (res.ok) return res.json();
      })
      .then((data) => {
        setNotes(data);
      });
  }
  useEffect(() => {
    ShowNotesFetch();
  }, []);

  return (
    <div className="container">


    <center>
    <h2>Notes Added</h2>
    </center>
      <div className="row ml-2">


      {notes.map((note) => {
        return (



          <div className="col-xs-3 m-3"  key={note.id}>
            <div className="card rounded" style={{width: "18rem"}}>
            <div className="card-body">
            <p>{readMore?note.text:`${note.text.substring(0,200)}...`}
                <button className="read-more" onClick={()=>setReadMore(!readMore)}>{readMore?'show less':'read more'}</button>
                </p>
              <button className="btn btn-outline-danger" onClick={deleteNoteHandler(note.id)}>Delete Note</button>

            </div>

          </div>
           </div>

        );
      })}
    </div>
    </div>
  );
}

export default ShowNotes;
