/* eslint-disable no-undef */
/* eslint-disable no-unused-vars */
import React,{useState,useEffect} from "react";

function AddNotes() {
  const [input,setInput]=useState("");
  let changeHandler=()=>{
    setInput(input);
  }
  return (
    <div className="container">
      <div class="form-group m-3">
        <label for="exampleFormControlTextarea2">Add Notes</label>
        <textarea
          class="form-control rounded-0"
          id="exampleFormControlTextarea2"
          placeholder="Add Your notes so that we can remind you"
          rows="3" value={input} onChange={()=>changeHandler}
        ></textarea>
        <button className=" btn-success rounded m-1 w-40">Add Note</button>
      </div>
    </div>
  );
}

export default AddNotes;
