/* eslint-disable react-hooks/exhaustive-deps */
/* eslint-disable no-undef */
/* eslint-disable no-unused-vars */
import React,{useState,useEffect} from "react";

function AddNotes() {
  const [note,setNotes]=useState('');
  const notesDetails={
    note,
  }
  const submitHandler=(e)=>{
    e.preventDefault();
    fetch("http://localhost:3100/notes",{
      method:'POST',
      headers :{
        'Content-Type':'application/json',
      },
      body: JSON.stringify(notesDetails),
    })
    .then((res)=>{
      if(res.ok)
        return res.json();
    })
  }
  return (
    <form onSubmit={e=>submitHandler(e)}>
    <div className="form-group">
      <p className="text-justify m-3">Enter the Notes</p>
    <textarea className="form-control w-50 m-3" id="exampleFormControlTextarea1" rows="3" onChange={e=>setNotes(e.target.value)}></textarea>
    <button className="btn btn-success m-3">Add Notes</button>
  </div>
  </form>
  );
}

export default AddNotes;
